import React, { useContext, useEffect, useState } from 'react'
import { AuthContext } from '../../context/AuthContext';
import * as authServices from '../../services/authServices';
import editUserValidation from '../../components/edit-user/editUserValidation';
import { useParams } from 'react-router-dom';

export default function useUserEdit() {

    const { id } = useParams();

    const { users, updateUser } = useContext(AuthContext);

    const [formError, setFormError] = useState({});
    const [formValue, setFormValue] = useS
    const [currentUser, setCurrentUser] = useState({
        email: '',
        username: '',
        image: '',
        password: '',
        reppass: '',
        wishReadBook: '',
    });




    console.log(currentUser)



    useEffect(() => {
        // Find the user data once it's available
        const user = users.find((x) => x.id === Number(id));

        if (user) {
            setCurrentUser({
                email: user.email,
                username: user.username,
                image: user.image,
                password: user.password,
                reppass: user.reppass,
                wishReadBook: user.wishReadBook,
            });
        }
    }, [id, users]);


    function onChange(e) {
        setCurrentUser(oldValue => ({
            ...oldValue,
            [e.target.name]: e.target.value
        }));
    }

    function onSubmit(event) {
        event.preventDefault();
        const error = editUserValidation(formValue);
        setFormError(error);

        if (Object.values(error).length === 0) {
            updateUser(id, formValue)
        }
    }

    return { formValue: currentUser, formError, onChange, onSubmit };
}
