import { createContext, useState } from "react";
import useGetAll from "../custom-hooks/hooks-requests/useGetAll";
import * as bookServices from "../services/bookServices";
import { useNavigate } from "react-router-dom";

export const BooksContext = createContext();

const BooksContextProvider = (props) => {

    const { books, setBooks, fetchError } = useGetAll([]);
    const [serverError, setServerError] = useState(null);
    const naviagte = useNavigate();

    async function bookCreate(book) {
        const result = await bookServices.create(book);
        if (result.message) {
            setServerError(result.message);
            return serverError;
        }

        setBooks(oldBooks => [...oldBooks, result]);
        naviagte('/');
    }

    async function bookUpdate(bookId, book) {
        const result = await bookServices.update(bookId, book);
        if (result.message) {
            setServerError(result.message);
            return serverError;
        }

        setBooks(books.map(x => x.id ));
        naviagte('/');
    }

    async function bookDelete(bookId) {

    }


    return (
        <BooksContext.Provider value={{ books, serverError, fetchError, bookCreate, bookUpdate, bookDelete }}>
            {props.children}
        </BooksContext.Provider>
    )
}

export default BooksContextProvider;
