import React, { useContext, useState } from 'react'
import { BooksContext } from '../../context/BooksContext';
import { AuthContext } from '../../context/AuthContext';
import editValidation from '../../components/edit-page/editValidation';

export default function useFormEdit(bookId) {
    
    const { bookUpdate } = useContext(BooksContext);

    const { user } = useContext(AuthContext);

    const [formError, setFormError] = useState({});

    const [formValue, setFormValue] = useState({
        title: '',
        author: '',
        genre: '',
        rating: '',
        image: '',
        description: '',
        ownerId: user.id
    });

    function onChange(e) {
        setFormValue(oldValue => ({
            ...oldValue,
            [e.target.name]: e.target.value
        }));
    }

    function onSubmit(event) {
        event.preventDefault();
        const error = editValidation(formValue);
        setFormError(error);

        if (Object.values(error).length === 0) {
            bookUpdate(book.id,  formValue);
        }
    }

    return { formValue, formError, onChange, onSubmit };
}
